  useEffect(() => {
    setFeaturedProducts(products.slice(0, 4));
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(price);
  };

  return (
    <div className="home">
      <section className="hero" style={{ background: heroSlides[currentSlide].color }}>
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">{heroSlides[currentSlide].title}</h1>
            <p className="hero-subtitle">{heroSlides[currentSlide].subtitle}</p>
            <div className="hero-buttons">
              <Link to="/products" className="btn btn-primary">Mua ngay</Link>
              <Link to="/about" className="btn btn-secondary">T√¨m hi·ªÉu th√™m</Link>
            </div>
          </div>
          <div className="hero-image">
            <img src={heroSlides[currentSlide].image} alt={heroSlides[currentSlide].title} />
          </div>
        </div>
        <div className="hero-dots">
          {heroSlides.map((_, index) => (
            <button
              key={index}
              className={`dot ${index === currentSlide ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
            />
          ))}
        </div>
      </section>

