            <div className="feature-card">
              <div className="feature-icon">üöö</div>
              <h3>Giao h√†ng mi·ªÖn ph√≠</h3>
              <p>Cho ƒë∆°n h√†ng t·ª´ 500K</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">üîÑ</div>
              <h3>ƒê·ªïi tr·∫£ 30 ng√†y</h3>
              <p>Mi·ªÖn ph√≠ ƒë·ªïi tr·∫£</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">‚úÖ</div>
              <h3>Ch√≠nh h√£ng 100%</h3>
              <p>Cam k·∫øt s·∫£n ph·∫©m ch√≠nh h√£ng</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">üéß</div>
              <h3>H·ªó tr·ª£ 24/7</h3>
              <p>T∆∞ v·∫•n m·ªçi l√∫c</p>
            </div>
          </div>
        </div>
      </section>

      <section className="featured-products">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">S·∫£n ph·∫©m n·ªïi b·∫≠t</h2>
            <Link to="/products" className="view-all">Xem t·∫•t c·∫£ ‚Üí</Link>
          </div>
          <div className="products-grid">
            {featuredProducts.map((product, index) => (
              <div key={product.id} className="product-card" style={{ animationDelay: `${index * 0.15}s` }}>
                <div className="product-image-wrapper">
                  <img src={product.image} alt={product.name} className="product-image" />
                  <div className="product-overlay">
                    <Link to={`/products/${product.id}`} className="view-btn">Xem chi ti·∫øt</Link>
                  </div>
                  <span className="product-category">{product.category}</span>
                </div>
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <div className="product-rating">
                    {'‚≠ê'.repeat(Math.floor(product.rating))}
                    <span className="rating-value">{product.rating}</span>
                  </div>
                  <span className="product-price">{formatPrice(product.price)}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>ƒêƒÉng k√Ω nh·∫≠n ∆∞u ƒë√£i</h2>
            <p>Nh·∫≠n ngay voucher gi·∫£m 10% cho ƒë∆°n h√†ng ƒë·∫ßu ti√™n</p>
            <div className="cta-form">
              <input type="email" placeholder="Nh·∫≠p email c·ªßa b·∫°n" className="cta-input" />
              <button className="btn btn-primary">ƒêƒÉng k√Ω</button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
